import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import './App.scss';

import logo from './pictures/logo.png'
import jeunesol from './pictures/1.png'



class Header extends Component {
  render() {
    return (
      <div>
        <div className="header">
          <div className="header-container">
            <div className="img-logo">
              <img src={logo}></img>
            </div>
            <div className="search-bar">
              <img class="search-icon" src="data:image/svg+xml;utf8;base64," />
              <input className="search" type="text" placeholder="Rechercher" autoFocus required></input>
            </div>
          </div>
        </div> 
      </div>
    )
  }
}

class Menu extends Component {
  render() {
    return (
      <div>
        <div className="menu-bar">
          <div className="menu-container">
            <div className="right-side-container">
              <button className="acceuil" type="button">Acceuil</button>
              <button className="actualités" type="button">Actualités</button>
              <button className="infos" type="button">Infos pratiques</button>
              <button className="covid" type="button">Covid</button>
            </div>
            <div clasName="left-side-container">
              <button className="inscrire" type="button">S'inscrire</button>
              <button className="se-connecter" type="button">Se connecter</button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

class Middle extends Component {


  constructor(props) {
    super(props);
    this.state = {value: 0};
    this.attestations = ["Attestation 1", "Attestation 2", "Attestation 3", "Attestation 4"];
    this.contents = ["TEXT 1 "," TEXT 2", "TEXT 3", "TEXT 4"];
    this.handleClick = this.handleClick.bind(this);
  }


  handleClick(event, i) {
    this.setState({value: i});
  }


  render() {
    return (
      <div className="page-content">
        <div className="banner">
          <a href="https://covidtracker.fr/">
            <img className="jeune-sol" src={jeunesol}></img>
          </a>
          <div className="column-position">
            <div className="columns">
              <div className="display-blocks">
                <div className="left-slides">
                  <a className="attestation" onClick={(e) => this.handleClick(e,0)}>
                    <span className="slide-content">Attestation 1</span></a>
                  <a className="attestation" onClick={(e) => this.handleClick(e,1)}>
                    <span className="slide-content">Attestation 2</span></a>
                  <a className="attestation" onClick={(e) => this.handleClick(e,2)}>
                    <span className="slide-content">Attestation 3</span></a>
                  <a className="attestation" onClick={(e) => this.handleClick(e,3)}>
                    <span className="slide-content">Attestation 4</span></a>
                </div>


                <div className="middle-slides">
                  <div className="middle-slides-content">
                    <div className="middle-slides-content-text">
                      <div className="slide-main-text">
                        <dl>
                          <dt>{this.contents[this.state.value]}</dt>
                          <dd>
                            <div className="publication-date">
                              Publié le 26 Avril 2021
                            </div>
                            <div className="publication-text">
                              <p>
                                {this.contents[this.state.value]}
                              </p>
                            </div>
                          </dd>
                        </dl>
                      </div>
                    </div>
                  </div>
                </div>



              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}


class App extends Component {
  render() {
    return (    
    <div>
      <Header />
      <hr />
      <Menu />
      <hr />
      <Middle />
    </div>
    );
  }

}

export default App;
