import React from 'react';
import {FormLabel}  from 'react-bootstrap';
import './App.css';

class Header extends React.Component {
  render() {
    return (
      <div>
        <h1>AWS S3 Standard vs S3 Infrequent Access</h1>
        <h4> <small>Cost Calculator (All units per month)</small></h4>
        <hr/>
      </div>
    );

  }
}

class Localisation extends React.Component {
  render() {
    return (
    <div>
      <select>
        <option>US East (N.Virginia)</option>
        <option>US West (Oregon)</option>
        <option>US West (N.California)</option>
        <option>EU (Ireland)</option>
      </select>
    </div>
    );
  }
}


class Table extends React.Component {

  constructor(props) {
    super(props);
    this.state = {value : Array(4).fill(0), sum: Array(3).fill(0)};
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event, i) {
    const newValue = this.state.value;
    const newSum = this.state.sum;
    newValue.splice(i,1,event.target.value);
    newSum[0] += parseFloat(newValue[i]);
    newSum[1] += parseFloat(20*newValue[i]);
    newSum[2] += parseFloat(30*newValue[i]);
    this.setState({value : newValue, sum : newSum});
  }


  render() {
    return (
    <div>
      <table>
        <tr>
          <th>Ressource</th>
          <th>Unit</th>
          <th>S3 Standard</th>
          <th>S3 Infrequent Access</th>
        </tr>
        <tr>
          <th>Data Size in TB</th>
          <th><textarea value={this.state.value[0]} onChange={(e) => this.handleChange(e,0)} rows="1" cols="20"></textarea></th>
          <th>${10*this.state.value[0]}</th>
          <th>${20*this.state.value[0]}</th>
        </tr>
        <tr>
          <th>No. of POST/PUT requests in Millions</th>
          <th><textarea value={this.state.value[1]} onChange={(e) => this.handleChange(e,1)} rows="1" cols="20"></textarea></th>
          <th>${20*this.state.value[1]}</th>
          <th>${30*this.state.value[1]}</th>
        </tr>
        <tr>
          <th>No. of GET requests in Millions</th>
          <th><textarea value={this.state.value[2]} onChange={(e) => this.handleChange(e,2)} rows="1" cols="20"></textarea></th>
          <th>${40*this.state.value[2]}</th>
          <th>${50*this.state.value[2]}</th>
        </tr>
        <tr>
          <th>S3 IA Data transfer cost in GB</th>
          <th><textarea value={this.state.value[3]} onChange={(e) => this.handleChange(e,3)} rows="1" cols="20"></textarea></th>
          <th>${60*this.state.value[3]}</th>
          <th>${70*this.state.value[3]}</th>
        </tr>
        <tr>
          <th>Totals</th>
          <th>${this.state.sum[0]}</th>
          <th>${this.state.sum[1]}</th>
          <th>${this.state.sum[2]}</th>
        </tr>
      </table>
    </div>
    );
  }
}


class Table2 extends React.Component {

  render() {
    return (
    <div>
      <div classname="tableHeader">
        <FormLabel bsClass="col-sm-3 text-left"> Resource {this.props.value} </FormLabel>
        <FormLabel bsClass="col-sm-3 text-left"> Unit </FormLabel>
        <FormLabel bsClass="col-sm-3 text-left"> S3 Standard </FormLabel>
        <FormLabel bsClass="col-sm-3 text-left"> S3 Infrequent Access </FormLabel>
      </div>
      <div classname="tableBody"></div>
      <div classname="tableFooter"></div>
    </div>
    );
  }
}

class App extends React.Component {
  render() {
    return (
      <div>
        <Header />
        <Localisation />
        <br></br>
        <Table />
        <Table2 value="Issam"/>
        <br></br>
      </div>

    );
  }
}

export default App;
