import React, {Component} from "react";
import './auth-css/auth.css';

import HeaderCitoyen from './HeaderCitoyen';

class DemandeCarte extends Component {


    constructor(props) {
        super(props);
        this.state ={
            selectedFile:null,
            userdata:{},
        }
        this.onFormSubmit = this.onFormSubmit.bind(this);
        this.onFileChange = this.onFileChange.bind(this)
    }
    onFileChange = event => { 
        // Update the state 
        this.setState({ selectedFile: event.target.files[0] }); 
      }; 
    // On file upload (click the upload button) 
    onFileUpload = () => { 
        // Create an object of formData 
        const formData = new FormData(); 

        // Update the formData object 
        formData.append( 
            "fichier", 
            this.state.selectedFile, 
            this.state.selectedFile.name 
        ); 

        // Details of the uploaded file 
        console.log(this.state.selectedFile); 

        // Request made to the backend api 
        // Send formData object 
        //fetch("api/uploadfile", formData); 
        }; 
    onFormSubmit(e){
        e.preventDefault() // Stop form submit

        // Ajout Citoyen
        fetch("http://localhost:8080/Appli-Web/project/AddCitoyen", {
        method: "post",
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          civilite:this.state.civilite,
          nom:this.state.nom,
          prenom:this.state.prenom,
          contact:null,
          dateDeNaissance:this.state.datedenaisssance,
          lieuDeNaissance:this.state.lieudenaissance,
          nationalite:this.state.nationalite,
          statut:this.state.statut,

        })
        })
        .then( (response) => { 
            //Récupérer ID de citoyen.
            const url = "http://localhost:8080/Appli-Web/project/GetCitoyenId/"+this.state.nom+"/"+this.state.prenom+"/"+this.state.datedenaisssance+"/"+this.state.lieudenaissance+"/"+this.state.nationalite;
            fetch(url, {
                method:"get",
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            }).then(response => response.json()).then((data) => 
            // Ajout de login de citoyen.
            fetch("http://localhost:8080/Appli-Web/project/AddLogin", {
            method: "post",
            headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
            },
            body: JSON.stringify({
            email:this.state.email,
            mdp:this.state.motdepasse,
            type:"CITOYEN",
            idLogin:data[0]["id"]
            })
            })
            .then( (response) => { 
                //do something awesome that makes the world a better place

            })
            );
        });
    }
    componentDidMount(){
        // Recuperer les informations de citoyen.
        const id = 1;
        const url = "http://localhost:8080/Web-Project/project/GetCitoyenById/"+id;
        fetch(url, {
        method: "get",
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }}).then(response => response.json()).then((data) => {
            // Pré-remplir le formulaire
            console.log(data);
            this.setState({userdata: data});
        });


    }
    render(){
        return(
          <div>
                <HeaderCitoyen id={this.props.location.state.id} />
                <form onSubmit={this.onFormSubmit}>
                    <h3>Demande de carte de séjour</h3>
                    <div className="form-group">
                        <label>Nom</label>
                        <input type="text" name="nom" className="form-control" disabled value={this.state.userdata["nom"]}/>
                    </div>
                    <br></br>
                    <div className="form-group">
                        <label>Prenom</label>
                        <input type="text" name="prenom" className="form-control" disabled value={this.state.userdata["prenom"]}/>
                    </div>
                    <br></br>
                    <div className="form-group">
                        <label>Date de naissance  </label>
                        <input type="date"  className="form-control" id="start" name="trip-start"
                                disabled
                                value={this.state.userdata["lieuDeNaissance"]}
                              />
                    </div>
                    <br></br>
                    <div className="form-group">
                    <label>Lieu de naissance </label>
                    <input type="text" name="lieudenaissance" className="form-control" disabled value={this.state.userdata["lieuDeNaissance"]} />
                    </div>
                    <br></br>
                    <div className="form-group">
                    <label>Nationalité </label>
                    <input type="text" name="nationalite" className="form-control" disabled value={this.state.userdata["nationalite"]}/>
                    </div>
                    <br></br>
                    <div className="form-group">
                    <label>Statut </label>
                    <input type="text" name="statut" className="form-control" value={this.state.userdata["statut"]}/>
                    </div>
                    <br></br>
                    <div className="form-group">
                    <label>Adresse </label>
                    <input type="text" name="adresse" className="form-control" value={this.state.userdata["adresse"]}/>
                    </div>
                    <br></br>
                    <div className="form-group">
                    <label>Revenu mensuel en Euro </label>
                    <input type="text" name="revenumensuel" className="form-control"/>
                    </div>
                    <br></br>
                    <div className="form-group">
                    <label>Documents</label>
                    <input type="file" onChange={this.onFileChange} /> 

                    </div>
                    <br></br>
                    <button type="submit" className="btn   btn-primary btn-block">Envoyer la demande</button>

                </form>
            </div>
        )
    }
}

export default DemandeCarte;
