import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import  {Button} from "@material-ui/core"
import './App.scss';


import Header from './Header';
import Attestations from './Attestations'


import recrutement from './pictures/recrutement.png'
import actualite from './pictures/actualite.png'




class News extends Component {

  render() {
    return (
      <div >
        <div className="news-column">
          <div className="spacer">
            <h2 className="actualites">Actualités</h2>
          </div>
            <hr className="separator"></hr>
        </div>

        <div className="news">

          <div className="news-container">
            <div className="news-1-container">
              <img className="news-image" src={actualite}></img>
              <div className="news-text-container">
                <h2> Actualité 1</h2>
                <p> ..........................................................
                ............................................................ </p>
                <a className="lire-suite" href="#"><em>Lire la suite</em></a>
              </div>
            </div>

            <hr className="separate-news"></hr>

            <div className="news-1-container">
              <img className="news-image" src={actualite}></img>
              <div className="news-text-container">
                <h2> Actualité 1</h2>
                <p> ..........................................................
                ............................................................ </p>
                <a className="lire-suite" href="#"><em>Lire la suite</em></a>
              </div>
            </div>

            <hr className="separate-news"></hr>

            <div className="news-1-container">
              <img className="news-image" src={actualite}></img>
              <div className="news-text-container">
                <h2> Actualité 1</h2>
                <p> ..........................................................
                ............................................................ </p>
                <a className="lire-suite" href="#"><em>Lire la suite</em></a>
              </div>
            </div>

          </div>

        </div>

      </div>
    );
  }

}

class Test extends Component {
  render() {
    return (
      <div className="test">
          <News />
      </div>
    );
  }
}

class Recrutement extends Component {
  render() {
    return (
      <div className="recrutement">
        <div className="recrutement-container">
          <img className="recrutement-image" src={recrutement}></img>
          <div className="recrutement-text-container">
            <h2> Le Ministère Recrute ! </h2>
            <p> Retrouvez ici toutes les informations concernant les métiers du Ministre de l'intérieur et les modes de recrutement.
            </p>
            <button className="rejoindre" type="button">Nous rejoindre</button>
          </div>
        </div>
        <div>
          <br></br>
            <em>Une question ? Besoin d'aide? Consulter notre aide en ligne ou contacter nous: </em> 
            <button className="aide" type="button">Consulter le centre d'aide</button>
        </div>
      </div>
    );
  }


}

class App extends Component {
  render() {
    return (    
    <div>
      <Header />
      <hr className="overall-separator" />
      <Attestations />
      <Test />
      <hr className="overall-separator"/>
      <Recrutement />
      <br></br>
      <div className="end">
        <center>Copyright <span>&#169;</span> 2021 tous les droits résérvés.</center>
      </div>

    </div>
    );
  }

}


export default App;
