import React, { Component } from 'react';
import Button from '@material-ui/core/Button';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';

import './App.scss';



import Header from './Header';
import Recrutement from './Recrutement';

const useStyles = makeStyles((theme) => ({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 200,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
  }));

class Gestion extends Component {
    render() {
        return (
            <div className="gestion-button-container">
                <Button className="gerer-demandes" variant="contained" color="default">Gérer les demandes</Button>
                <Button className="gerer-demandes" variant="contained" color="default">Demandes traitées</Button>
                
            </div>
            
        )
    }
}

  
 function ChoixDemandes() {
    const classes = useStyles();
    const [age, setAge] = React.useState('');
  
    const handleChange = (event) => {
      setAge(event.target.value);
    };
  
    return (
      <div className="gestion-demande-container">
        <div className="type-demande">
        <FormControl className={classes.formControl}>
          <InputLabel id="demo-simple-select-label">Type de demande</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={age}
            onChange={handleChange}
            autoWidth="true"
          >
            <MenuItem value={10}>Carte nationale d'identité</MenuItem>
            <MenuItem value={20}>Passeport</MenuItem>
            <MenuItem value={30}>Immatriculation des véhicules</MenuItem>
          </Select>
        </FormControl>
        </div>
        <div className="date"><TextField id="standard-basic" label="Date" /></div>

      </div>
    );
  }


  function choixQuestions() {
    const classes = useStyles();
    const [age, setAge] = React.useState('');
  
    const handleChange = (event) => {
      setAge(event.target.value);
    };
  
    return (
      <div className="gestion-demande-container">
        <div className="type-demande">
        <FormControl className={classes.formControl}>
          <InputLabel id="demo-simple-select-label">Type de question</InputLabel>
          <Select
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={age}
            onChange={handleChange}
            autoWidth="true"
          >
            <MenuItem value={10}>Carte nationale d'identité</MenuItem>
            <MenuItem value={20}>Passeport</MenuItem>
            <MenuItem value={30}>Immatriculation des véhicules</MenuItem>
          </Select>
        </FormControl>
        </div>
        <div className="date"><TextField id="standard-basic" label="Date" /></div>

      </div>
    );
  }

class Demandes extends Component {
    render() {
        return (
            <div className="demandes-container">
                <div className="demandes-1-container">
                    <div className="demandes-content-container">
                        <div className="demande-text">
                            <span> Demande 1 ...... </span>
                        </div>
                        <div className="demande-buttons">
                            <button className="cosulter" type="button">Consulter</button>
                            <button className="traiter" type="button">Traiter</button>
                            <button className="traite-par" type="button">Traité par : Aucun</button>
                        </div>
                    </div>
                </div>
                <hr className="separate-demandes"></hr>

            </div>
        )
    }
}

class Questions extends Component {
    render() {
        return (
            <div className="demandes-container">
                <div className="demandes-1-container">
                    <div className="demandes-content-container">
                        <div className="demande-text">
                            <span> Demande 1 ...... </span>
                        </div>
                        <div className="demande-buttons">
                            <button className="Répondre" type="button">Consulter</button>
                        </div>
                    </div>
                </div>
                <hr className="separate-demandes"></hr>

            </div>
        )
    }
}

class PageDemandes extends Component {
  render() {
    return (    
    <div>
      <Header />
      <hr className="overall-separator" />
      <Gestion content="salut"/>
      
      <ChoixDemandes />
      <Demandes />
      <Demandes />
      <Demandes />
      <Demandes />
      <Demandes />
      <Demandes />
      <Demandes />
      <Demandes />
      <Demandes />
      <choixQuestions />

      <Recrutement />
      <br></br>
      <div className="end">
        <center>Copyright <span>&#169;</span> 2021 tous les droits résérvés.</center>
      </div>

    </div>
    );
  }

}
export default PageDemandes;
